"CREATE TABLE IF NOT EXISTS `user` (`id` UUID PRIMARY KEY, `email` VARCHAR(255) NOT NULL, `password` VARCHAR(255) NOT NULL, `username` VARCHAR(255) NOT NULL, `phone` NUMBER NOT NULL, `firstName` VARCHAR(255), `lastName` VARCHAR(255), `gender` VARCHAR(255), `country` VARCHAR(255), `city` VARCHAR(255), `address` VARCHAR(255), `bankAccount` NUMBER, `bankAccountType` VARCHAR(255), `bankName` VARCHAR(255), `dobMonth` VARCHAR(255), `dobDay` NUMBER, `dobYear` NUMBER, `isAdmin` TINYINT, `status` TINYINT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);"